/** * *@paramyear 年份 *@return返回纳音 */
+ (NSString * ) getnumsix: (NSString * ) year {
    for (int i = 0; i < 60; i++) {
        if ([year  isEqualToString: self.numsix[i][0]]) {
            return self.numsix[i][1];
        }
    }
    return @"";
}
/** * 胎神 *@paramday 日柱 *@return.. */
+ (NSString * ) getTai: (NSString * ) day {
    for (int i = 0; i < 60; i++) {
        if ([day  isEqualToString: self.numsix[i][0]]) {
            return self.numsix[i][2];
        }
    }
    return nil;
}
/** * 吉凶 *@paramday 日柱 *@return.. */
+ (NSString * ) getJiXiong: (NSString * ) day {
    for (int i = 0; i < 60; i++) {
        if ([day  isEqualToString: self.numsix[i][0]]) {
            return self.numsix[i][5];
        }
    }
    return nil;
}
/** * 冲煞 *@paramday 日柱 *@return.. */
+ (NSString * ) getChongSha: (NSString * ) day {
    for (int i = 0; i < 60; i++) {
        if ([day  isEqualToString: self.numsix[i][0]]) {
            return self.numsix[i][3];
        }
    }
    return nil;
}
//获取五行日纳音
+ (NSString * ) wuxingRi: (NSString * ) marr {
    NSArray * array = [marr componentsSeparatedByString: @","];
    NSString * ganziday = array[2]; //日柱
    return ganziday;
    
}
+(void)marriage:(NSString*)marr{
    NSArray  *array = [marr componentsSeparatedByString:@","];
    NSString* ganziyear = array[0];//年柱
    NSString* ganzimonth = array[1];//月柱
    NSString* ganziday = array[2];//日柱
    NSString* ganzitime = array[3];//时柱    //五行纳音
    NSString* soundyear = [self getnumsix:ganziyear];
    NSString* soundmonth = [self getnumsix:ganzimonth];
    NSString* soundday = [self getnumsix:ganziday];
    NSString* soundtime = [self getnumsix:ganzitime];
    NSLog(@"年：%@。 月：%@  日：……%@  时：%@",soundyear,soundmonth,soundday,soundtime);
    NSLog(@"五行：%@",soundyear);
    NSString* tai = [self getTai:ganziday];
    NSLog(@"胎神：%@",tai);
    NSString* jx = [self getJiXiong:ganziday];
    NSLog(@"吉凶：%@",jx);
    NSString* cs = [self getChongSha:ganziday];
    NSLog(@"冲煞：%@",cs);
    
}

+(NSString *)getWuxing:(NSString*)marr{
  return  [self getnumsix:marr];
}

